import  {useState, useEffect, useMemo} from 'react'

import {Input, PartList, Spinner} from '../components';

import './PageSelection.css'


import { FaLongArrowAltLeft } from "react-icons/fa";

import { Link, useLocation } from 'react-router-dom';


import { getHardware } from '../api/restApi';



interface Part {
  name: string,
  img: string,
  link: string,
  price?: string,
  features?: Object,
}

type Hardware = {
  hardware: "videocards" | "processors" | "cases" | "coolers" | "hdd" | "moutherboards" | "power" | "ram" | "ssd";
}


 const PageSelection = () => {


    const location = useLocation();

    const hardware = location.state;

    console.log(hardware);
    
    


    const [parts, setParts] = useState<Part[]>([]);
    const [spinner, setSpinner] = useState(true);



    const handleHardware = async (hardware : Hardware) => {
        const parts = await getHardware(hardware)
        setParts(parts);
        setSpinner(false);
    }




    useEffect(  () => {

      console.log('Маунт');
 
      getHardware(hardware).then(parts => setParts(parts))

      return () => {
        console.log('Анмаунт');
  
      }

    }, [])


    // const filterParts = () => {
    // let filtredParts = parts.filter(part => {
    //     if (!!part.img && !!part.link && !!part.price && !!part.name) {
    //       return part;
    //     } 
    //   })
    //   setParts(filtredParts);
    // }



    console.log(!parts);
    


  return (
    <div className='pageSelection'>
      <Link className='arrowBack'  to='/'>
      <FaLongArrowAltLeft size={98} />
      </Link>
        <Input className="input"/>
        {
          !parts ? <PartList parts={parts} />  : spinner
        }
    </div>
  )
}

export default PageSelection;
