import  {useState, useEffect, useMemo} from 'react'

import {Input, PartList} from '../components';

import './PageSelection.css'


import { FaLongArrowAltLeft } from "react-icons/fa";

import { Link, useLocation } from 'react-router-dom';


import { getHardware } from '../api/restApi';
import { ColorRing } from 'react-loader-spinner'


interface Part {
  name: string,
  img: string,
  link: string,
  price?: string,
  features?: Object,
}


 const PageSelection = () => {


    const location = useLocation();

    const hardware = location.state;

    console.log(hardware);
    
    


    const [parts, setParts] = useState<Part[]>([]);





    useEffect(  () => {

      console.log('Маунт');
 
      getHardware(hardware).then(parts => setParts(parts))

      return () => {
        console.log('Анмаунт');
  
      }

    }, [])


    // const filterParts = () => {
    // let filtredParts = parts.filter(part => {
    //     if (!!part.img && !!part.link && !!part.price && !!part.name) {
    //       return part;
    //     } 
    //   })
    //   setParts(filtredParts);
    // }


    const spinner = <ColorRing
    visible={true}
    height="80"
    width="80"
    ariaLabel="color-ring-loading"
    wrapperStyle={{}}
    wrapperClass="color-ring-wrapper"
    colors={['#e15b64', '#f47e60', '#f8b26a', '#abbd81', '#849b87']}
    />


  return (
    <div className='pageSelection'>
      <Link className='arrowBack'  to='/'>
      <FaLongArrowAltLeft size={98} />
      </Link>
        <Input className="input"/>
        {
          !!parts ? <PartList parts={parts} />  : spinner
        }
    </div>
  )
}

export default PageSelection;
