import  {useState, useEffect, useMemo, memo, useCallback} from 'react'

import {Input, PartList, Spinner} from '../components';

import './PageSelection.css'


import { FaLongArrowAltLeft } from "react-icons/fa";

import { Link, useLocation } from 'react-router-dom';


import { getHardware } from '../api/restApi';
import Pagination from '../components/pagination/Input/Pagination';



interface Part {
  name: string,
  img: string,
  link: string,
  price?: string,
  features?: Object,
}

type Hardware = {
  hardware: "videocards" | "processors" | "cases" | "coolers" | "hdd" | "moutherboards" | "power" | "ram" | "ssd";
}


 const PageSelection = () => {



  
    
   const location = useLocation();
   const hardware = location.state;

   console.log(hardware);
   

    const [parts, setParts] = useState<Part[]>([]);
    const [spinner, setSpinner] = useState(true);
    const [page, setPage] = useState(1);




    const ROWS_PER_PAGE = 10;

    const getTotalPageCount = (rowCount: number): number =>
      Math.ceil(rowCount / ROWS_PER_PAGE);
    
    const handleNextPageClick = useCallback(() => {
      const current = page;
      const next = current + 1;
      const total = parts ? getTotalPageCount(parts.length) : current;
    
      setPage(next <= total ? next : current);
    }, [page, parts]);
    
    const handlePrevPageClick = useCallback(() => {
      const current = page;
      const prev = current - 1;
    
      setPage(prev > 0 ? prev : current);
    }, [page]);

  


    const fetchData = async (hardware : Hardware) => {

      console.log(1);
      
      const parts = await getHardware({hardware, page} )  
      setParts(parts);
      setSpinner(false);
  }

    
      
    



    useEffect(  () => {

      fetchData(hardware);

    }, [page])


    // const filterParts = () => {
    // let filtredParts = parts.filter(part => {
    //     if (!!part.img && !!part.link && !!part.price && !!part.name) {
    //       return part;
    //     } 
    //   })
    //   setParts(filtredParts);
    // }



    console.log(parts);
    


  return (
    <div className='pageSelection'>
      <Link className='arrowBack'  to='/'>
      <FaLongArrowAltLeft size={98} />
      </Link>
        <Input className="input"/>
        { spinner ? <Spinner className="spinner"/> : <PartList parts={parts} />}
        {
          !spinner && parts && (
            <Pagination
          onNextPageClick={handleNextPageClick}
          onPrevPageClick={handlePrevPageClick}
          disable={{
            left: page === 1,
            right: page === getTotalPageCount(parts.length),
          }}
          nav={{ current: page, total: getTotalPageCount(parts.length) }}
        />
      )}
      </div>
    
  ) 
  }

export default memo(PageSelection);
