import { Link } from 'react-router-dom';
import cn from 'classnames';
import styles from './LinkTag.module.css';
import background from '../../images/ym.png'

type Props = {
  className?: string; 
  children?: string | JSX.Element;
  sayHello?: any,
  hardware?: "videocards" | "processors" | "cases" | "coolers" | "hdd" | "moutherboards" | "power" | "ram" | "ssd",
  path: string;

}


export default function LinkTag({children, path, className, ...props} : Props) : JSX.Element {


  const { hardware } = props;

  const inlineStyle = hardware ? {} : {backgroundImage: `url(${background})`, backgroundRepeat:"no-repeat", backgroundSize:"contain", height: 50, width: 50}


  return (
    <Link  to={path} style={inlineStyle} className={cn(className, styles.btn)} state={hardware}>{children}</Link>
  )
}
