import  {useState, useEffect, useMemo, memo, useCallback} from 'react'

import {Input, PartList, Spinner} from '../components';

import './PageSelection.css'

import _ from 'lodash'

import { FaLongArrowAltLeft } from "react-icons/fa";

import { Link, useLocation, useParams } from 'react-router-dom';


import { getHardware } from '../api/restApi';
import Pagination from '../components/pagination/Input/Pagination';
import useDebounce from '../hooks/useDebounce.js';



interface Part {
  name: string,
  img: string,
  link: string,
  price?: string,
  features?: Object,
}





 const PageSelection = () => {



  
    

    const {hardware = '', id = 1}  = useParams();

  
    const [parts, setParts] = useState<Part[]>([]);
    const [data, setData] = useState<Part[]>([]);
    const [spinner, setSpinner] = useState(true);
    const [page, setPage] = useState(Number(id));
    const [searchItem, setSearchItem] = useState('');
    const debouncedSearchTerm = useDebounce(searchItem, 500);


    const filterParts = (parts : Part[], searchItem : string) => {

      if (searchItem === '') {
        setData(parts.slice((page-1)*ROWS_PER_PAGE, page*ROWS_PER_PAGE))
        return;
      }
     
       const filtredItems = parts.filter((part) => {
 
        return part.name.toLowerCase().includes(searchItem.toLowerCase())
       })

       return filtredItems;
    

    } 


    const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {


      e.preventDefault();
      setSearchItem(e.target.value);
      filterParts(parts, searchItem);
      setData(filtredParts.slice((page-1)*ROWS_PER_PAGE, page*ROWS_PER_PAGE))
      setPage(1);
      }

    
  
  
    

    const ROWS_PER_PAGE = 25;

    const getTotalPageCount = (rowCount: number): number =>
      Math.ceil(rowCount / ROWS_PER_PAGE);
    
    const handleNextPageClick = useCallback(() => {
      const current = page;
      const next = page + 1;
      const total = filtredParts ? getTotalPageCount(filtredParts.length) : current;
    
      setPage(next <= total ? next : current);
    }, [page, filtredParts]);
    
    const handlePrevPageClick = useCallback(() => {
      const current = page;
      const prev = current - 1;
    
      setPage(prev > 0 ? prev : current);
    }, [page]);

  
    

    const fetchData = async (hardware : string, page : number) => {



      

      const parts = await getHardware(hardware, page)  

      setSpinner(false);

      setParts(parts);
      filterParts(parts, searchItem);

  }

    

    useEffect(  () => {
      
       
     if (debouncedSearchTerm) {
    
      setSpinner(true);
      fetchData(hardware, page);
     }
      
      
      

    }, [page])



  return (
    <div className='pageSelection'>
      <Link className='arrowBack'  to='/'>
      <FaLongArrowAltLeft size={98} />
      </Link>
        <Input value={searchItem} onChange={(e) => handleSearch(e)} className="input"/>
        { spinner ? <Spinner className="spinner"/> : <PartList parts={data} />}
        {
          !spinner && data && (
            <Pagination
          onNextPageClick={handleNextPageClick}
          onPrevPageClick={handlePrevPageClick}
          disable={{
            left: page === 1,
            right: page === getTotalPageCount(filtredParts.length),
          }}
          nav={{ current: page, total: getTotalPageCount(filtredParts.length), hardware }}
        />
      )}
      </div>
    
  )
        }

export default memo(PageSelection);
